mapper.select:
select count(id) 
from project_info 
where status=#{period}

select count(id) 
from project_process 
where status=#{status} AND project_id=#{projectId}

select count(id) 
from project_process 
where project_id=#{projectId}

select pp.id, pp.project_id, pi.project_name, pi.project_num, pp.period, pp.period_name, pp.period_leader_id, pp.status, pp.period_start_date, pp.period_end_date, pp.create_time, pp.update_time from project_period as pp join project_info as pi on(pp.project_id=pi.id)
and pp.period_leader_id=#{leaderId}
and pi.project_name like '%${qwv.projectName}%'
and pi.project_num like '%${qwv.projectNum}%'
and pp.status = #{qwv.status}

mapper.update:
update 
project_info 
set status=#{period}
where id=#{projectId}

update project_info 
set status=0 
where id=#{id}
